import logo from './logo.svg';
import './App.css';
import React from 'react';
import {getAllStudents} from './client';
import {useState, useEffect} from 'react';
import type { SizeType } from 'antd/es/config-provider/SizeContext';
import StudentDrawerForm from './StudentDrawerForm';
import { QuestionCircleOutlined } from '@ant-design/icons';
import { Popconfirm } from 'antd';

import { Layout, Menu, Breadcrumb, Table, Spin, Empty, Button, Badge,Tag, Avatar} from 'antd';
import {
    DesktopOutlined,
    PieChartOutlined,
    FileOutlined,
    TeamOutlined,
    UserOutlined,
    LoadingOutlined,
    PlusOutlined
} from '@ant-design/icons';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu; 
const TheAvatar = ({name}) => {
	let trim = name.trim();
	let idx = name.indexOf(' ');
	if(trim.length === 0) {
		return <Avatar icon={<UserOutlined/>}/>
	}
	const split = trim.split(" ");
	if (split.length === 1) {
		return <Avatar>{name.charAt(0)}</Avatar>
	}
	return <Avatar> 
		{`${name.charAt(0)}${name.charAt(idx+1)}`} 
	</Avatar>
};
const TheActions = ({id}) => {
  return <>
  <Popconfirm title="Delete the task" 
	  description="Are you sure to delete this student?" 
	  icon={<QuestionCircleOutlined style={{ color: 'red',}}/>}>
	  <Radio.Group value={size} onChange={(e) => setSize(e.target.value)}>
        <Radio.Button value="large">Delete {id}</Radio.Button>
        <Radio.Button value="default">Edit</Radio.Button>
      </Radio.Group>
  </Popconfirm>

	</>

}; 







const columns = [
  {
    title: '',
    dataIndex: 'avatar',
    key: 'avatar',
    render: (text, student) => 
    	 <TheAvatar name={student.name}/> 
  },
  {
    title: 'Id',
    dataIndex: 'id',
    key: 'id',
  },
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: 'Email',
    dataIndex: 'email',
    key: 'email',
  },
  {
    title: 'Gender',
    dataIndex: 'gender',
    key: 'gender',
  },
    {
    title: 'Actions',
    dataIndex: 'deleteEdit',
    key: 'deleteEdit',
    render: (number, student) => 
    	 <TheActions id={student.id}/> 
  }
];
const antIcon = (
  <LoadingOutlined
    style={{
      fontSize: 24,
    }}
    spin
  />
);


function App() {
	const [students, setStudents] = useState ([]);
	const [collapsed, setCollapsed] = useState(false);
	const [fetching, setFetching] = useState(false);
	const [showDrawer, setShowDrawer] = useState(false);

	const fetchStudents = () => 
		getAllStudents()
		.then(res => res.json())
		.then(data => {
			console.log(students);
			setStudents(data);
		})

	useEffect(() => {
		fetchStudents();
	},[]

	);

	const renderStudents = () => {
		if(fetching){
			return <Spin indicator={antIcon} />
		}
		if(students.length <= 0){
			return <Empty />
		}
		return <>
			<StudentDrawerForm
          		showDrawer={showDrawer}
            	setShowDrawer={setShowDrawer}
            	fetchStudents={fetchStudents}
          	/>
			<Table dataSource={students} columns={columns}
				bordered           
				title = {()=> 
					<>
			        	<small className = "stNo" > Number of students </small>
						<Badge count={students.length} className="site-badge-count-4"/>
			     		<br/><br/>
			     		<Button 
			     			value="small"
							onClick={() => setShowDrawer(!showDrawer)}
							type="primary" shape="round" icon={<PlusOutlined />} size="32px">
				             Add New Student  
			        	</Button>
			     	</>
			     }
			     pagination ={{pageSize:50}} 
				 scroll={{y:500}} rowKey = {(student) => student.id}
			/>
		</>
	}

  return <Layout style={{ minHeight: '100vh' }}>
        <Sider collapsible collapsed={collapsed}
               onCollapse={setCollapsed}>
            <div className="logo" />
            <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
                <Menu.Item key="1" icon={<PieChartOutlined />}>
                    Option 1
                </Menu.Item>
                <Menu.Item key="2" icon={<DesktopOutlined />}>
                    Option 2
                </Menu.Item>
                <SubMenu key="sub1" icon={<UserOutlined />} title="Student">
                    <Menu.Item key="3">Tom</Menu.Item>
                    <Menu.Item key="4">List</Menu.Item>
                    <Menu.Item key="5">Alex</Menu.Item>
                </SubMenu>
                <SubMenu key="sub2" icon={<TeamOutlined />} title="Team">
                    <Menu.Item key="6">Team 1</Menu.Item>
                    <Menu.Item key="8">Team 2</Menu.Item>
                </SubMenu>
                <Menu.Item key="9" icon={<FileOutlined />}>
                    Files
                </Menu.Item>
            </Menu>
        </Sider>
        <Layout className="site-layout">
            <Header className="site-layout-background" style={{ padding: 0 }} />
            <Content style={{ margin: '0 16px' }}>
                <Breadcrumb style={{ margin: '16px 0' }}>
                    <Breadcrumb.Item>Student</Breadcrumb.Item>
                    <Breadcrumb.Item>List</Breadcrumb.Item>
                </Breadcrumb>
                <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                    {renderStudents()}
                </div>
            </Content>
            <Footer style={{ textAlign: 'center' }}>By Cadence IT</Footer>
        </Layout>
    </Layout>
}

export default App;
